// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

contract CampaignContract {

    // Enums and Structs
    enum ProjectCategory { 
        NFT, DAO, DEFI, GAMEFI, Wallet, Exchange, Community, Infrastructure, VC, Web3, KOL, Metaverse, Other 
    }

    struct Campaign {
        uint id;
        address creator;
        string name;
        uint startTimestamp;
        uint endTimestamp;
        string image;
        string description;
        uint tokenReward;
        uint points;
        uint minimumLevel;
        uint minimumBalance;
        uint numberOfWinners;
        string tasksURL;
        bool tasks;
        bool visibility;
        address[] participants;
        uint randomnessBlockNumber;
    }

    struct Event {
        uint id;
        address host;
        string name;
        uint startTimestamp;
        uint endTimestamp;
        string image;
        string description;
        uint tokenReward;
        uint points;
        uint minimumCampaign;
        uint numberOfWinners;
        uint[] campaignIds;
        address[] participants;
    }

    struct User {
        address userAddress;
        string name;
        uint assets;
        uint points;
        uint level;
        uint[] spaceJoined;
        uint[] campaignsJoined;
        uint[] eventsJoined;
    }

    struct Space {
        string name;
        address creator;
        string image;
        ProjectCategory category;
        string officialWebsite;
        string discordLink;
        string telegramLink;
        string description;
        address[] followers;
        uint[] campaigns;
        uint[] events;
    }

    struct LeaderboardEntry {
        uint campaignsJoined;
        uint eventsJoined;
        uint totalPoints;
    }

    // Events
    event CampaignCreated(uint id, address indexed creator);
    event EventCreated(uint id, address indexed host);
    event SpaceCreated(uint id, address indexed creator);
    event UserRegistered(address indexed userAddress, string name);
    event UserUpdated(address indexed userAddress, string name);
    event JoinedCampaign(uint indexed campaignId, address indexed user);
    event JoinedEvent(uint indexed eventId, address indexed user);
    event JoinedSpace(uint indexed spaceId, address indexed user);

    // Mappings
    mapping(uint => Campaign) public campaigns;
    mapping(uint => Event) public events;
    mapping(address => User) public users;
    mapping(uint => Space) public spaces;
    mapping(uint => mapping(uint => bool)) public campaignInSpace; // spaceId => (campaignId => bool)
    mapping(uint => mapping(uint => bool)) public eventInSpace; // spaceId => (eventId => bool)
    mapping(address => mapping(uint => bool)) public userJoinedCampaign; // user => (campaignId => bool)
    mapping(address => mapping(uint => bool)) public userJoinedEvent; // user => (eventId => bool)
    mapping(address => mapping(uint => bool)) public userJoinedSpace; // user => (spaceId => bool)
    // Mapping to store campaign IDs created by each address
    mapping(address => uint[]) public campaignsByCreator;

    // Mapping to store event IDs created by each address
    mapping(address => uint[]) public eventsByHost;

    // Mapping to store space IDs created by each address
    mapping(address => uint[]) public spacesByCreator;

    mapping(uint => mapping(address => LeaderboardEntry)) public spaceLeaderboard;

    // Counters
    uint public campaignCount;
    uint public eventCount;
    uint public spaceCount;

    // Constructor
    constructor() {}

    // Modifiers
    modifier timeCheck(uint startTimestamp, uint endTimestamp) {
        require(startTimestamp >= block.timestamp, "Start time must be in the future");
        require(endTimestamp > startTimestamp, "End time must be after start time");
        _;
    }

    modifier isActive(uint startTimestamp, uint endTimestamp) {
        require(block.timestamp >= startTimestamp, "This has not started yet");
        require(block.timestamp <= endTimestamp, "This has already ended");
        _;
    }

    // Deposit function
    function deposit() public payable {
        if (users[msg.sender].userAddress == address(0)) {
            // Register new user if not already registered
            registerNewUser(msg.sender, msg.value);
        } else {
            // User is already registered, add to their assets
            users[msg.sender].assets += msg.value;
        }
    }

    function withdraw(uint amount) public {
        require(amount > 0, "Amount must be greater than zero");
        require(users[msg.sender].assets >= amount, "Insufficient balance");
    
        users[msg.sender].assets -= amount;
        (bool sent, ) = msg.sender.call{value: amount}("");
        require(sent, "Failed to send Ether");
    }

    // Create Campaign Function
    function createCampaign(
        string memory name,
        uint startTimestamp,
        uint endTimestamp,
        string memory image,
        string memory description,
        uint tokenReward,
        uint points,
        uint minimumLevel,
        uint minimumBalance,
        uint numberOfWinners,
        string memory tasksURL,
        bool visibility
    ) public timeCheck(startTimestamp, endTimestamp) {
        require(users[msg.sender].assets >= tokenReward, "Insufficient assets to create campaign");

    // Deduct token amount from user's assets
    users[msg.sender].assets -= tokenReward;
        campaignCount++;
        Campaign storage newCampaign = campaigns[campaignCount];
        newCampaign.id = campaignCount;
        newCampaign.creator = msg.sender;
        newCampaign.name = name;
        newCampaign.startTimestamp = startTimestamp;
        newCampaign.endTimestamp = endTimestamp;
        newCampaign.image = image;
        newCampaign.description = description;
        newCampaign.tokenReward = tokenReward;
        newCampaign.points = points;
        newCampaign.minimumLevel = minimumLevel;
        newCampaign.minimumBalance = minimumBalance;
        newCampaign.numberOfWinners = numberOfWinners;
        newCampaign.tasksURL = tasksURL;
        newCampaign.visibility = visibility;


        campaignsByCreator[msg.sender].push(newCampaign.id);

        emit CampaignCreated(newCampaign.id, msg.sender);
    }

    // Create Event Function
    function createEvent(
        string memory name,
        uint startTimestamp,
        uint endTimestamp,
        string memory image,
        string memory description,
        uint tokenReward,
        uint points,        
        uint minimumCampaign,
        uint numberOfWinners,
        uint[] memory campaignIds
    ) public timeCheck(startTimestamp, endTimestamp) {
        require(users[msg.sender].assets >= tokenReward, "Insufficient assets to create event");

    // Deduct token amount from user's assets
    users[msg.sender].assets -= tokenReward;
        eventCount++;
        Event storage newEvent = events[eventCount];
        newEvent.id = eventCount;
        newEvent.host = msg.sender;
        newEvent.name = name;
        newEvent.startTimestamp = startTimestamp;
        newEvent.endTimestamp = endTimestamp;
        newEvent.image = image;
        newEvent.description = description;
        newEvent.tokenReward = tokenReward;
        newEvent.points = points;
        newEvent.minimumCampaign = minimumCampaign;
        newEvent.numberOfWinners = numberOfWinners;
        newEvent.campaignIds = campaignIds;

        eventsByHost[msg.sender].push(newEvent.id);

        emit EventCreated(newEvent.id, msg.sender);
    }

    // Create Space Function
    function createSpace(
        string memory name,
        string memory image,
        ProjectCategory category,
        string memory officialWebsite,
        string memory discordLink,
        string memory telegramLink,
        string memory description,
        uint[] memory campaignIds,
        uint[] memory eventIds
    ) public {
        spaceCount++;
        Space storage newSpace = spaces[spaceCount];
        newSpace.name = name;
        newSpace.creator = msg.sender;
        newSpace.image = image;
        newSpace.category = category;
        newSpace.officialWebsite = officialWebsite;
        newSpace.discordLink = discordLink;
        newSpace.telegramLink = telegramLink; // optional
        newSpace.description = description;

        for (uint i = 0; i < campaignIds.length; i++) {
            require(campaigns[campaignIds[i]].creator == msg.sender, "Can add only owned campaigns");
            newSpace.campaigns.push(campaignIds[i]);
            campaignInSpace[spaceCount][campaignIds[i]] = true;
        }
        for (uint i = 0; i < eventIds.length; i++) {
            require(events[eventIds[i]].host == msg.sender, "Can add only owned events");
            newSpace.events.push(eventIds[i]);
            eventInSpace[spaceCount][eventIds[i]] = true;
        }
        spacesByCreator[msg.sender].push(spaceCount);
    
    }

    // Function to join a campaign

    function joinCampaign(uint campaignId) public isActive(campaigns[campaignId].startTimestamp, campaigns[campaignId].endTimestamp) {
        if (users[msg.sender].userAddress == address(0)) {
        // Register new user if not already registered
        registerNewUser(msg.sender, 0);
    }
        require(users[msg.sender].userAddress != address(0), "User does not exist");
        require(!hasJoinedCampaign(msg.sender, campaignId), "Already joined this campaign");

        Campaign storage campaign = campaigns[campaignId];
        require(users[msg.sender].level >= campaign.minimumLevel, "User level too low");
        require(users[msg.sender].assets >= campaign.minimumBalance, "Insufficient balance");

        campaign.participants.push(msg.sender);
        users[msg.sender].campaignsJoined.push(campaignId);

        uint spaceId = findSpaceIdForCampaign(campaignId);
        if (spaceId != 0) {
            LeaderboardEntry storage entry = spaceLeaderboard[spaceId][msg.sender];
            entry.campaignsJoined++;
            entry.totalPoints += campaigns[campaignId].points;
        }

        // Update mapping to reflect user's participation in the campaign
        userJoinedCampaign[msg.sender][campaignId] = true;

        emit JoinedCampaign(campaignId, msg.sender);
    }

    // Function to join an event
    function joinEvent(uint eventId) public isActive(events[eventId].startTimestamp, events[eventId].endTimestamp) {
        require(users[msg.sender].userAddress != address(0), "User does not exist");
        require(!hasJoinedEvent(msg.sender, eventId), "Already joined this event");
        if (users[msg.sender].userAddress == address(0)) {
        // Register new user if not already registered
        registerNewUser(msg.sender, 0);
    }


        Event storage event_ = events[eventId];
        uint completedCampaigns = 0;
        for (uint i = 0; i < event_.campaignIds.length; i++) {
            if (hasJoinedCampaign(msg.sender, event_.campaignIds[i])) {
                completedCampaigns++;
            }
        }
        require(completedCampaigns >= event_.minimumCampaign, "Not enough campaigns completed");

        event_.participants.push(msg.sender);
        users[msg.sender].eventsJoined.push(eventId);

        uint spaceId = findSpaceIdForEvent(eventId);
        if (spaceId != 0) {
            LeaderboardEntry storage entry = spaceLeaderboard[spaceId][msg.sender];
            entry.eventsJoined++;
            entry.totalPoints += events[eventId].points;
        }

        // Update mapping to reflect user's participation in the event
        userJoinedEvent[msg.sender][eventId] = true;

        emit JoinedEvent(eventId, msg.sender);
    }

    // Function to join a space
    function joinSpace(uint spaceId) public {
        require(users[msg.sender].userAddress != address(0), "User does not exist");
        require(!hasJoinedSpace(msg.sender, spaceId), "Already joined this space");

        Space storage space = spaces[spaceId];
        space.followers.push(msg.sender);
        users[msg.sender].spaceJoined.push(spaceId);
        // Update mapping to reflect user's joining of the space
        userJoinedSpace[msg.sender][spaceId] = true;

        emit JoinedSpace(spaceId, msg.sender);
    }

    // Function to edit a campaign
    function editCampaign(
        uint campaignId,
        string memory newName,
        string memory newImage,
        string memory newDescription,
        uint newTokenReward,
        uint newPoints,
        uint newMinimumLevel,
        uint newMinimumBalance,
        uint newNumberOfWinners,
        string memory newTasksURL,
        bool newVisibility
    ) public {
        Campaign storage campaign = campaigns[campaignId];
        require(msg.sender == campaign.creator, "Only the creator can edit this campaign");

        campaign.name = newName;
        campaign.image = newImage;
        campaign.description = newDescription;
        campaign.tokenReward = newTokenReward;
        campaign.points = newPoints;
        campaign.minimumLevel = newMinimumLevel;
        campaign.minimumBalance = newMinimumBalance;
        campaign.numberOfWinners = newNumberOfWinners;
        campaign.tasksURL = newTasksURL;
        campaign.visibility = newVisibility;
    }

    // Function to edit an event
    function editEvent(
        uint eventId,
        string memory newName,
        string memory newImage,
        string memory newDescription,
        uint newTokenReward,
        uint newPoints,
        uint newMinimumCampaign,
        uint newNumberOfWinners,
        uint[] memory newCampaignIds
    ) public {
        Event storage event_ = events[eventId];
        require(msg.sender == event_.host, "Only the host can edit this event");

        event_.name = newName;
        event_.image = newImage;
        event_.description = newDescription;
        event_.tokenReward = newTokenReward;
        event_.points = newPoints;
        event_.minimumCampaign = newMinimumCampaign;
        event_.numberOfWinners = newNumberOfWinners;
        event_.campaignIds = newCampaignIds;
    }

    // Function to register a new user
// Internal function to handle new user registration
    function registerNewUser(address userAddress, uint initialDeposit) internal {
        User storage newUser = users[userAddress];
        newUser.userAddress = userAddress;
        newUser.name = ""; // Empty name for now
        newUser.assets = initialDeposit; // Initialize with the deposit amount
        newUser.points = 0;
        newUser.level = 1;
        newUser.spaceJoined = new uint[](0);
        newUser.campaignsJoined = new uint[](0);
        newUser.eventsJoined = new uint[](0);

        emit UserRegistered(userAddress, "");
    }

    // Function to update a user's name
    function updateUserName(string memory newName) public {
        // Check for user existence and string emptiness
        require(users[msg.sender].userAddress != address(0), "User does not exist");
        require(bytes(newName).length > 0, "New name cannot be empty");

        users[msg.sender].name = newName;
        emit UserUpdated(msg.sender, newName);
    }

    // Fallback or receive function to handle direct Ether transfers
    receive() external payable {
        if (users[msg.sender].userAddress == address(0)) {
            // Register new user if not already registered
            registerNewUser(msg.sender, msg.value);
        } else {
            // User is already registered, add to their assets
            users[msg.sender].assets += msg.value;
        }
    }

    function finalizeRandomSelection(uint campaignId) public {
        Campaign storage campaign = campaigns[campaignId];
        require(block.timestamp > campaign.endTimestamp, "Campaign has not ended yet");
        require(block.number > campaign.randomnessBlockNumber, "Randomness block not reached");
        require(campaign.participants.length > 0, "No participants");

        bytes32 blockHash = blockhash(campaign.randomnessBlockNumber);
        require(blockHash != 0, "Blockhash not available");

        uint numberOfWinners = campaign.numberOfWinners;
        if (numberOfWinners == 0 || numberOfWinners > campaign.participants.length) {
            numberOfWinners = campaign.participants.length;
        }

        for (uint i = 0; i < numberOfWinners; i++) {
            uint randomIndex = uint(keccak256(abi.encodePacked(blockHash, i))) % campaign.participants.length;
            address winnerAddress = campaign.participants[randomIndex]; 

            User storage winner = users[winnerAddress];
            winner.assets += campaign.tokenReward / numberOfWinners;
            winner.points += campaign.points;

            updateLevel(winnerAddress); // Update the user's level

            // Remove winner from participants
            campaign.participants[randomIndex] = campaign.participants[campaign.participants.length - 1];
            campaign.participants.pop();
    }
    }

    function updateLevel(address userAddress) private {
    User storage user = users[userAddress];

    // Example calculation: 100 points per level
    uint newLevel = user.points / 100;

    // Assuming a maximum level cap (if any)
    if (newLevel > 10) { // Set your max level cap here
        newLevel = 10;
    }

    // Update the level if it has increased
    if (newLevel > user.level) {
        user.level = newLevel;
    }
}

    // Efficient hasJoined Functions
    function hasJoinedCampaign(address user, uint campaignId) internal view returns (bool) {
        return userJoinedCampaign[user][campaignId];
    }

    function hasJoinedEvent(address user, uint eventId) internal view returns (bool) {
        return userJoinedEvent[user][eventId];
    }

    function hasJoinedSpace(address user, uint spaceId) internal view returns (bool) {
        return userJoinedSpace[user][spaceId];
    }

     // Utility Functions to Find Space ID
    function findSpaceIdForCampaign(uint campaignId) internal view returns (uint) {
        for (uint i = 1; i <= spaceCount; i++) {
            if (campaignInSpace[i][campaignId]) {
                return i;
            }
        }
        return 0;
    }

    function findSpaceIdForEvent(uint eventId) internal view returns (uint) {
        for (uint i = 1; i <= spaceCount; i++) {
            if (eventInSpace[i][eventId]) {
                return i;
            }
        }
        return 0;
    }

    // Getter function for Campaign details
    function getCampaign(uint campaignId) public view returns (Campaign memory, address[] memory) {
        Campaign storage campaign = campaigns[campaignId];
        require(campaign.creator != address(0), "Campaign not found");
        return (campaign, campaign.participants);
    }

    // Getter function for Event details
    function getEvent(uint eventId) public view returns (Event memory, uint[] memory, address[] memory) {
        Event storage event_ = events[eventId];
        require(event_.host != address(0), "Event not found");
        return (event_, event_.campaignIds, event_.participants);
    }

    // Getter function for User details
    function getUser(address userAddress) public view returns (User memory) {
        User storage user = users[userAddress];
        return user;
    }

    // Getter function for Space details
    function getSpace(uint spaceId) public view returns (Space memory, address[] memory, uint[] memory, uint[] memory) {
        Space storage space = spaces[spaceId];
        return (space, space.followers, space.campaigns, space.events);
    }

    // Getter function for Leaderboard Entry details
    function getLeaderboardEntry(uint spaceId, address userAddress) public view returns (LeaderboardEntry memory) {
        LeaderboardEntry storage entry = spaceLeaderboard[spaceId][userAddress];
        return entry;
    }

// Function to get total counts of campaigns, events, and spaces
function getTotalCounts() public view returns (uint, uint, uint) {
    return (campaignCount, eventCount, spaceCount);
}

// Functions to list all IDs of campaigns, events, or spaces
function getAllCampaignIDs() public view returns (uint[] memory) {
    uint[] memory ids = new uint[](campaignCount);
    for (uint i = 1; i <= campaignCount; i++) {
        ids[i - 1] = i;
    }
    return ids;
}

function getAllEventIDs() public view returns (uint[] memory) {
    uint[] memory ids = new uint[](eventCount);
    for (uint i = 1; i <= eventCount; i++) {
        ids[i - 1] = i;
    }
    return ids;
}

function getAllSpaceIDs() public view returns (uint[] memory) {
    uint[] memory ids = new uint[](spaceCount);
    for (uint i = 1; i <= spaceCount; i++) {
        ids[i - 1] = i;
    }
    return ids;
}

// Function to get all participants of a campaign or event
function getCampaignParticipants(uint campaignId) public view returns (address[] memory) {
    return campaigns[campaignId].participants;
}

function getEventParticipants(uint eventId) public view returns (address[] memory) {
    return events[eventId].participants;
}

// Function to retrieve campaign IDs created by a specific address
    function getCampaignsByCreator(address creator) public view returns (uint[] memory) {
        return campaignsByCreator[creator];
    }

    // Function to retrieve event IDs created by a specific address
    function getEventsByHost(address host) public view returns (uint[] memory) {
        return eventsByHost[host];
    }

    // Function to retrieve space IDs created by a specific address
    function getSpacesByCreator(address creator) public view returns (uint[] memory) {
        return spacesByCreator[creator];
    }
}
